mapper.delete:
DELETE FROM like_record
WHERE user_id = ${likeRecord.userId}
  AND target_id = ${likeRecord.userId.targetId}
  AND `target_type` = ${likeRecord.userId.targetType};

mapper.select:
SELECT COUNT(1)
FROM like_record
WHERE user_id = ${likeRecord.userId}
  AND target_id = ${likeRecord.userId.targetId}
  AND `target_type` = ${likeRecord.userId.targetType};
